import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { Ticket } from '../../../models/ticket';
import { Major } from '../../../models/enum';

@Component({
  selector: 'app-ticket',
  templateUrl: './ticket.component.html',
  styleUrls: ['./ticket.component.scss']
})
export class TicketComponent implements OnInit {

  /**
   * Inputs & Output allow communication between parent & child components.
   * More information: https://angular.io/guide/component-interaction
   */
  @Input()
  ticket!: Ticket;
  @Output()
  ticketHasBeenSelected: EventEmitter<boolean> = new EventEmitter<boolean>();

  @Output()
  ticketHasBeenArchived: EventEmitter<Ticket> = new EventEmitter<Ticket>();

  constructor() {
  }

  ngOnInit() {
  }
  
  public get major(): typeof Major {
    return Major;
  }

  selectTicket() {
    this.ticketHasBeenSelected.emit(true);
  }

  archivedTicket(ticket: Ticket) {
    this.ticketHasBeenArchived.emit(ticket);
  }
}


<div class="card ticket" (click)="selectTicket()">
  <!-- we use the {{ }} syntax to display the data, more info: https://angular.io/guide/displaying-data-->
  <h3>Titre : {{ticket.title}}</h3>
  <i class="fas fa-calendar-alt"></i> {{ticket.date | date: 'shortTime'}} &nbsp;&nbsp;
  <i class="far fa-user" *ngIf="ticket.student"></i>
  <div >
    {{ticket.student.firstName}} {{ticket.student.lastName}} &nbsp;&nbsp;
  </div>
  <div *ngIf="ticket.major == 'SI'">
    <i class="fas fa-wine-bottle"></i>
  </div>
  <div *ngIf="ticket.major == 'GE'">
    <i class="fas fa-wheelchair"></i>
  </div>
  <div *ngIf="ticket.major == 'GB'">
    <i class="fas fa-walking"></i> 
  </div>
  {{ticket.major}}
  <button type="submit" class="btn-custom" (click)="archivedTicket(ticket)">Archived</button>
</div>
