import { Component, OnInit } from '@angular/core';
import { TicketService } from '../../../services/ticket/ticket.service';
import { Ticket } from '../../../models/ticket';

@Component({
  selector: 'app-ticket-list',
  templateUrl: './ticket-list.component.html',
  styleUrls: ['./ticket-list.component.scss']
})
export class TicketListComponent implements OnInit {

  public ticketList: Ticket[] = [];

  constructor(public ticketService: TicketService) {
    this.ticketService.tickets$.subscribe((tickets) => this.ticketList = tickets);
  }

  ngOnInit() {
  }

  ticketHasBeenSelected(hasBeenSelected: boolean) {
    console.log('event received from child:', hasBeenSelected);
  }

  archivedTicket(ticket: Ticket) {
    // console.log(ticket);
    this.ticketService.archivedTicket(ticket);
  }

}
<div class="ticket-list">
  <!-- <div class="ticket" *ngFor="let ticket of ticketList">
      <app-ticket [ticket]="ticket" (ticketHasBeenArchived)="archivedTicket($event)"></app-ticket>
  </div> -->
  <button class="btn-custom" (click)="displayTicketArchived = !displayTicketArchived">
    {{displayTicketArchived ? 'Hide' : 'Show'}} the Tickets
  </button>
  <div class="mt-2" *ngFor="let ticket of Ticket_List">
    <span *ngIf="!ticket.archived || displayTicketArchived">
      <app-ticket [ticket]="ticket" (ticketHasBeenSelected)="ticketHasBeenSelected($event)"
        (ticketHasBeenArchived)="archiveTicket($event)"></app-ticket>
    </span>
  </div>
</div>
